mapper.select:


             and token = #{token}
             and order_id = #{orderId}
             and page_id = #{pageId}
             and node_code = #{nodeCode}
             and user_id = #{userId}
             and product_code = #{productCode}
             and product_name like concat('%', #{productName}, '%')


				and date_format(begin_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


				and date_format(begin_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


             and file_path = #{filePath}
             and client_type = #{clientType}
             and client_ip = #{clientIp}
             and client_city_code = #{clientCityCode}
             and client_city_name like concat('%', #{clientCityName}, '%')
             and client_browser = #{clientBrowser}
             and status = #{status}
             and create_user = #{createUser}
             and update_user = #{updateUser}
             and flag = #{flag}
             and oper_type = #{operType}
             and policy_no = #{policyNo}
             and policy_name like concat('%', #{policyName}, '%')
             and policy_idcard = #{policyIdcard}
             and node_url = #{nodeUrl}
             and sys_code = #{sysCode}
             and sdk_type = #{sdkType}

            order by create_time desc

mapper.update:
update eye_record_page_his
token = #{token}, order_id = #{orderId}, page_id = #{pageId}, node_code = #{nodeCode}, user_id = #{userId}, product_code = #{productCode}, product_name = #{productName}, begin_time = #{beginTime}, end_time = #{endTime}, record_time = #{recordTime}, play_set_time = #{playSetTime}, file_path = #{filePath}, client_type = #{clientType}, client_ip = #{clientIp}, client_city_code = #{clientCityCode}, client_city_name = #{clientCityName}, client_browser = #{clientBrowser}, status = #{status}, create_user = #{createUser}, create_time = #{createTime}, update_user = #{updateUser}, update_time = #{updateTime}, flag = #{flag}, remark = #{remark}, oper_type = #{operType}, policy_no = #{policyNo}, policy_name = #{policyName}, policy_idcard = #{policyIdcard}, node_url = #{nodeUrl}, sys_code = #{sysCode}, sdk_type = #{sdkType},
where id = #{id}

update eye_record_page_his
product_name = #{productName}, update_user = #{updateUser}, update_time = #{updateTime}, flag = #{flag}, policy_no = #{policyNo}, policy_name = #{policyName}, policy_idcard = #{policyIdcard},
where order_id = #{orderId}

update eye_record_page_his
order_id = #{orderId}, product_name = #{productName}, update_user = #{updateUser}, update_time = #{updateTime}, flag = #{flag}, policy_no = #{policyNo}, policy_name = #{policyName}, policy_idcard = #{policyIdcard},
where product_code = #{productCode} AND token = #{token}

mapper.select:
select eye_record_page_his.id, eye_record_page_his.order_id, eye_record_page_his.token, eye_record_page_his.order_id, eye_record_page_his.node_code, eye_record_page_his.user_id, eye_record_page_his.product_code, eye_record_page_his.product_name, eye_record_page_his.begin_time, eye_record_page_his.end_time, eye_record_page_his.record_time, eye_record_page_his.file_path, eye_record_page_his.create_user, eye_record_page_his.create_time, sys_dict_data.dict_label as nodeName from eye_record_page_his LEFT JOIN sys_dict_data ON sys_dict_data.dict_value = eye_record_page_his.node_code
and eye_record_page_his.token = #{token}
order by eye_record_page_his.create_time asc
